
public class datatype {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	    boolean flag = true;
	    System.out.println(flag);    // boolean type
	    
	    byte range;
	    range = 124;
	    System.out.println(range);    //byte type
	    short temperature;
	    temperature = -200;
	    System.out.println(temperature); //short type 
	    
	    int r = -4250000;
	    System.out.println(r);  // int type
	    
	    long l = -42332200000L;
	    System.out.println(l);    // long type
	    
	    double number = -42.3;
	    System.out.println(number);  // double type
	    
	    float n = -42.3f;
	    System.out.println(n);  // float type
	    
	    char letter = '\u0051';
	    System.out.println(letter);  // char type
	    
	    String myString = "Java Programming";
       System.out.println(myString); //string type



	    

	
	}

}
